void SiONVoicePresetUtil::_generate_extra_voices() {

	_begin_category("KS");
	_create_ks_voice("extra.ks", "Bright KS",
		/*feedback*/0,  /*tension*/8,
		/*wave*/SiONPulseGeneratorType::PULSE_NOISE_PINK,  /*AR*/63, /*DR*/44,
		/*SL*/0,  /*RR*/28,  /*TL*/4);

	_create_ks_voice("extra.ks.soft_pluck", "Soft Pluck KS",
		/*feedback*/2,  /*tension*/4,
		/*wave*/SiONPulseGeneratorType::PULSE_NOISE_WHITE,
		/*AR*/55, /*DR*/36, /*SL*/4, /*RR*/22, /*TL*/6);

	_begin_category("FM (OPL)");
	// AL, FB,
	// (WS,AR,DR,RR,ET,SL,TL,KR,KL,ML,AM)  2
	_create_opl_voice("extra.opl", "Init OPL", {
		0,0,
		0, 60, 28, 28, 0, 0,  0, 0, 0, 1, 0,   // operator-1
		0, 60, 28, 28, 0, 0,  0, 0, 0, 1, 0     // operator-2
	});

	_create_opl_voice("extra.opl.brass", "Brass OPL", {
		// AL  FB
		 1,   3,
		//      WS AR DR RR ET SL TL KR KL ML AM
		 0,   60, 24, 16, 0, 4, 32, 0, 0, 2, 0,    // mod
		 1,   60, 32, 16, 0, 0,  0, 0, 0, 1, 0     // car
	});

	_begin_category("FM (OPM)");
	// AL, FB,
	// (DT,ML,TL,KS,AR,DR,SR,RR,SL,AM,DT2)  4
	_create_opm_voice("extra.opm", "Init OPM", {
		0,0,
		0,0,  0,0,15,8,0,2,0,0,0,   // op-1
		0,0,  0,0,15,8,0,2,0,0,0,   // op-2
		0,0,  0,0,15,8,0,2,0,0,0,   // op-3
		0,0,  0,0,15,8,0,2,0,0,0    // op-4
	});  // 2 + 114 = 46 ints
	
	// ------------------ FM (OPM) ------------------
	_create_opm_voice("extra.opm.epiano", "E.Piano OPM", {
		// AL FB
		3, 4,
		//        DT ML TL KS AR DR SR RR SL AM DT2
		0,  2, 31, 0, 15,  8,  0, 4, 6, 0, 0,   // op-1  bell modulator
		0,  3, 31, 0, 15,  8,  0, 4, 6, 0, 0,   // op-2  bell modulator 2
		0,  1,  0, 0, 15, 10,  0, 5, 6, 0, 0,   // op-3  carrier
		0,  1,  0, 0, 15, 10,  0, 5, 6, 0, 0    // op-4  carrier
	});

	_begin_category("FM (OPX)");
	// AL, FB,
	// (WS,AR,DR,SR,RR,SL,TL,KR,ML,D1,D2,AM)  4
	_create_opx_voice("extra.opx", "Init OPX", {
		0,0,
		0, 30, 16, 0, 34, 0, 0, 0, 1, 0, 0, 0,   // op-1
		0, 30, 16, 0, 34, 0, 0, 0, 1, 0, 0, 0,   // op-2
		0, 30, 16, 0, 34, 0, 0, 0, 1, 0, 0, 0,   // op-3
		0, 30, 16, 0, 34, 0, 0, 0, 1, 0, 0, 0    // op-4
	});                                             // 2 + 124 = 50 ints

	// --- Custom preset with pitch envelope ---------------------------------
	{
		Ref<SiONVoice> voice = memnew(SiONVoice);
		voice->set_analog_like(0, 0, 0, 0, 0);          // simple dual-saw patch
		
		// Create pitch envelope: start at +12 semitones, decay to 0 over 12 steps
		Vector<int> pitch_env;
		for (int i = 0; i < 12; i++) {
			int pitch_value = 768 - (768 * i / 11);   // Linear decay from 768 (12 semitones) to 0
			pitch_env.append(pitch_value);
		}
		pitch_env.append(0);  // Sustain at 0
		voice->set_pitch_envelope(
			make_typed_array_from_vector<int>(pitch_env),   // <- wrap here
			12);                                            // loop index
		
		voice->set_name("Swoop Lead (Pitch Env)");
		_register_voice("extra.opx.swooplead", voice);
	}

	// ------------------ FM (OPX) ------------------
	_create_opx_voice("extra.opx.pad", "Warm Pad OPX", {
		// AL FB
		5, 3,
		// WS AR DR SR RR SL TL KR ML D1 D2 AM
		0, 24, 16, 16, 18, 5, 40, 0, 2, 0, 0, 0,   // op-1
		1, 24, 16, 16, 18, 5, 48, 0, 1, 0, 0, 0,   // op-2
		0, 28, 18, 16, 18, 5, 44, 0, 2, 0, 0, 0,   // op-3
		1, 20, 14, 16, 18, 5, 44, 0, 1, 0, 0, 0    // op-4
	});

	_begin_category("PCM");
	_create_pcm_voice("extra.pcm.init", "PCM Init");

	// Template init presets  one per voice/chip type.
	// These are minimal "blank canvas" voices used as starting points for each synthesis engine.
	// Users can select them from the TEMPLATES category in the instrument browser.
	// The runtime build system uses them as init presets when constructing instruments
	// from voiceParams (voiceType -> init preset -> apply voiceParams on top).

	_begin_category("template");

	// SiOPM (default FM): single sine oscillator
	_create_basic_voice("template.siopm", "Init SiOPM", 0);

	// Analog-Like: dual-osc, both sine, no detune
	_create_analog_voice("template.analog", "Init Analog", 0, 0, 0, 0, 8);

	//  Init preset policy 
	// Algorithm 0 (serial stack): op0  op1    last op  output.
	// Last op = carrier (TL low = loud), all others = modulators (TL max = silent).
	// Result: clean audible sine. Users raise modulator TL to add FM.
	// Envelopes: fast AR, moderate DR/RR, full sustain on carrier.

	// OPN: 4-op FM, algorithm 0, feedback 0
	// Param layout: AL, FB, (AR[0-63],DR[0-63],SR[0-63],RR[0-63],SL[0-15],TL[0-127],KR[0-3],ML[0-15],D1[0-7],AM[0-3])  4
	_create_opn_voice("template.opn", "Init OPN", {
		0,0,
		//  AR DR SR RR  SL  TL   KR ML D1 AM
		63, 0, 0, 63, 15,   0, 0, 1, 0, 0    // op3 (carrier)  audible sine
	});

	// OPL: 2-op FM, algorithm 0, feedback 0
	// Param layout: AL, FB, (WS[0-31],AR[0-63],DR[0-63],RR[0-63],ET[0-1],SL[0-15],TL[0-63],KR[0-3],KL[0-3],ML[0-15],AM[0-3])  2
	_create_opl_voice("template.opl", "Init OPL", {
		0,0,
		// WS AR DR RR ET SL  TL  KR KL ML AM
		0, 60, 0, 60, 0, 15,  0, 0, 0, 1, 0   // op1 (carrier)  audible sine
	});

	// OPM: 4-op FM, algorithm 0, feedback 0
	// Param layout: AL, FB, (AR[0-63],DR[0-63],SR[0-63],RR[0-63],SL[0-15],TL[0-127],KR[0-3],ML[0-15],D1[0-7],D2[0-3],AM[0-3])  4
	_create_opm_voice("template.opm", "Init OPM", {
		0,0,
		//  AR DR SR RR  SL  TL   KR ML D1 D2 AM
		63, 8, 0, 63, 15,   0, 0, 1, 0, 0, 0    // op3 (carrier)  audible sine
	});

	// OPX: 4-op FM, algorithm 0, feedback 0
	// Param layout: AL, FB, (WS[0-7],AR[0-63],DR[0-63],SR[0-63],RR[0-63],SL[0-15],TL[0-127],KR[0-3],ML[0-15],D1[0-7],D2[],AM[0-3])  4
	_create_opx_voice("template.opx", "Init OPX", {
		0,0,
		// WS AR DR SR RR  SL  TL   KR ML D1 D2 AM
		0, 62, 0, 0, 34, 15,   0, 0, 1, 0, 0, 0    // op3 (carrier)  audible sine
	});

	// MA3: 4-op FM, algorithm 4 (serial stack  maps to internal alg 0), feedback 0
	// NOTE: MA3 4-op does NOT support AL=0 or AL=1 (both map to -1 in the algo table).
	// AL=4 is the first valid value for 4-op MA3, giving the simplest serial routing.
	// Param layout (engine-range): AL[0-7], FB[0-7], (WS[0-31],AR[0-63],DR[0-63],SR[0-63],RR[0-63],SL[0-15],TL[0-63],KR[0-3],KL[0-3],ML[0-15],D1[0-7],AM[0-3])  4
	_create_ma3_voice("template.ma3", "Init MA3", {
		4,0,
		// WS AR DR SR RR SL  TL  KR KL ML D1 AM
		0, 63, 0, 0, 63, 15,  0, 0, 0, 1, 0, 0    // op3 (carrier)  audible sine
	});

	// KS Guitar: basic pluck with pink noise excitation
	_create_ks_voice("template.ks", "Init KS",
		/*feedback*/0,  /*tension*/8,
		/*wave*/SiONPulseGeneratorType::PULSE_NOISE_PINK,
		/*AR*/63, /*DR*/44, /*SL*/0, /*RR*/28, /*TL*/4);

	// SCC (wave table): wave shape 0, sustained tone.
	// Requires INCLUDE_WAVETABLE to have run first (populates _wave_tables).
	if (!_wave_tables.is_empty()) {
		_create_wave_table_voice("template.scc", "Init SCC", 0, 63, 0, 0, 63, 15, 0);
	}
}

void SiONVoicePresetUtil::_create_ks_voice(const String &p_key,
	const String &p_name,
	int p_feedback,
	int p_delay,          //  will be used as tension
	int p_wave_shape,
	int p_attack_rate,
	int p_decay_rate,
	int p_sustain_level,
	int p_release_rate,
	int p_total_level) {

// reinterpret p_delay as Karplus-Strong damping / tension (0-15)
int tension = p_delay;

// Build a proper KS pluck  noise burst, delay-line, damping, etc.
Ref<SiONVoice> voice = memnew(SiONVoice);
voice->set_pms_guitar(
	/*attack_rate */ p_attack_rate,
	/*decay_rate  */ p_decay_rate,
	/*total_level */ p_total_level,
	/*fixed_pitch */ 69,                        // A4 reference; transposed per-note later
	/*wave_shape  */ p_wave_shape,
	/*tension     */ tension);

// Fine-tune feedback, sustain and release.
Ref<SiOPMChannelParams> ch_params = voice->get_channel_params();
ch_params->set_feedback(p_feedback);

Ref<SiOPMOperatorParams> op_params = ch_params->get_operator_params(0);
op_params->set_sustain_level(p_sustain_level);
op_params->set_release_rate(p_release_rate);

voice->set_name(p_name);
_register_voice(p_key, voice);
}

